import React, {useContext} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 440,
    textAlign:"left"
  },
  
  select:{
    width:"550px"
  }
}));

export default function SimpleSelect() {
  const classes = useStyles();

  const {countries, setChoosenCountry, changeCountry} = useContext(CovidContext)
  const handleChange = (event) => {
    setChoosenCountry(event.target.value);
  };



  return (
<div style={{textAlign:"center"}}>
      <FormControl className={classes.formControl}>
        <InputLabel id="demo-simple-select-helper-label">Choose Country</InputLabel>
        <Select
          labelId="demo-simple-select-helper-label"
          id="demo-simple-select-helper"
          value={changeCountry}
          onChange={handleChange}
          className={classes.select}
        >
          {countries.map((cur,ind)=>{
            return  <MenuItem value={cur.name} key={ind}>{cur.name}</MenuItem>
          })}
         
        </Select>
      </FormControl>
    </div>
  );
}
