import axios from "axios"

export const CovidContext = createContext();

export const CovidContextProvider = (props)=>{

    const [country, setChoosenCountry] = useState('');

    const [covidData, setCovidData] = useState({});
    // Fetch Covid Data

    async function fetchData() {
        const response = await axios.get(`https://covid19.mathdro.id/api/countries/${country}`);

        console.log(response.data);

    }


    return(
        <CovidContext.Provider value={{country, setChoosenCountry}}>
            {props.children}
        </CovidContext.Provider>
    )
}
