import React, {useContext} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import {CovidContext} from "../Context/CovidContext"

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap'
  },
  paper:{
    backgroundColor:"white",
    width:"250px",
    height:"220px",
    margin:"20px 20px",
    padding:"20px"
    },
  confirmedPaper:
  {
    borderBottom:"10px solid blue",
  },
  deathPaper:{
    borderBottom:"10px solid red"
  },
  recoveredPaper:{
    borderBottom:"10px solid green"
  },
 
}));

export default function SimplePaper() {
  const classes = useStyles();


  const {covidData} = useContext(CovidContext);

  const {confirmed, deaths, recovered, lastUpdate} = covidData;


  console.log("coviddata here", covidData);

  if(covidData===undefined)
  {
    return <h1>Fetching Data</h1>
  }
else{
  return (
    <div className={classes.root}>
      <Paper elevation={3} className={`${classes.paper} ${classes.confirmedPaper}`} >
         <h2 className="title">Confirmed</h2>
         <h1 className="cases-counts">{covidData.confirmed.value.toLocaleString()}</h1>
         <h3>{covidData.lastUpdate}</h3>
         <p className="info">Number of active cases in Covid-19</p>
        </Paper>
      <Paper elevation={3} className={`${classes.paper} ${classes.deathPaper}`} >
      <h2 className="title">Deaths</h2>
         <h1 className="cases-counts">this2</h1>
         <h3>Wed 23,2020</h3>
         <p className="info">Number of active cases in Covid-19</p></Paper>
      <Paper elevation={3} className={`${classes.paper} ${classes.recoveredPaper}`} >
      <h2 className="title">Deaths</h2>
         <h1 className="cases-counts">this3</h1>
         <h3>Wed 23,2020</h3>
         <p className="info">Number of active cases in Covid-19</p>
         </Paper>
    </div>
  );
}
}
